import React, { useState } from 'react';
import Navbar from './Navbar';
import { FiInfo } from 'react-icons/fi';
import './MainPage.css';

const MainPage = ({ userName, onPowerButtonClick }) => {
  const [inputText, setInputText] = useState('');
  const [quote, setQuote] = useState('');
  const [author, setAuthor] = useState('');
  const [emotion, setEmotion] = useState('');
  const [quoteEmotion, setQuoteEmotion] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const handleSubmit = async () => {
    const response = await fetch('http://127.0.0.1:5000/get_quote', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ inputText }),
    });
    const data = await response.json();
    setQuote(data.quote);
    setAuthor(data.author);
    setEmotion(data.emotion);
    setQuoteEmotion(data.quote_emotion);
    setInputText('');
  };

  const showModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="page-container flex flex-col items-center justify-center h-screen bg-green-900 text-white">
      <Navbar onPowerButtonClick={onPowerButtonClick} />
      <h1 className="header-text text-4xl mb-4">Hello {userName},</h1>
      <p className="subheader-text text-xl mb-8">What do you feel today?</p>
      <div className="input-container flex items-center relative">
        <input 
          type="text" 
          className="input-field p-2 px-4 pr-10 w-96 rounded-full bg-amber-100 text-black text-xl border-none outline-none"
          value={inputText}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          placeholder="Type here..." 
        />
        <button className="submit-button absolute right-4 bg-none border-none cursor-pointer text-black text-xl" onClick={handleSubmit}>â†’</button>
      </div>
      {quote && (
        <div className="quote-container mt-8 bg-red-400 p-4 rounded-md text-xl text-white text-center w-4/5 relative">
          <p className="mb-2" style={{ fontFamily: 'Merriweather, serif' }}>"{quote}"</p>
          <p style={{ fontFamily: 'Merriweather, serif' }}>- {author}</p>
          <div className="info-icon absolute top-2 right-2 cursor-pointer" onClick={showModal}>
            <FiInfo size={24} />
            <div className="tooltip">Check the emotion</div>
          </div>
        </div>
      )}

      {isModalOpen && (
        <div className="modal-overlay fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="modal bg-white text-black p-6 rounded-md">
            <h2 className="text-2xl mb-4">Emotion Details</h2>
            <p>{`User Emotion: ${emotion}`}</p>
            <p>{`Quote Emotion: ${quoteEmotion}`}</p>
            <button className="mt-4 p-2 bg-red-500 text-white rounded" onClick={closeModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default MainPage;
