import React, { useState } from 'react';
import Navbar from './Navbar';
import './MainPage.css'; // Import the CSS file

const MainPage = ({ userName, onPowerButtonClick }) => {
  const [inputText, setInputText] = useState('');
  const [quote, setQuote] = useState('');
  const [author, setAuthor] = useState('');
  const [emotion, setEmotion] = useState('');

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleSubmit = async () => {
    const response = await fetch('http://127.0.0.1:5000/get_quote', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ inputText }),
    });
    const data = await response.json();
    setQuote(data.quote);
    setAuthor(data.author);
    setEmotion(data.emotion);
    setInputText(''); // Clear the input field
  };

  return (
    <div className="page-container">
      <Navbar onPowerButtonClick={onPowerButtonClick} />
      <h1 className="header-text">Hello {userName},</h1>
      <p className="subheader-text">What do you feel today?</p>
      <div className="input-container">
        <input 
          type="text" 
          className="input-field" 
          value={inputText}
          onChange={handleInputChange}
          placeholder="Type here..." 
        />
        <button className="submit-button" onClick={handleSubmit}>â†’</button>
      </div>
      {quote && (
        <div className="quote-container">
          <p>"{quote}"</p>
          <p>{author}</p>
          <p>{`Detected Emotion: ${emotion}`}</p>
        </div>
      )}
    </div>
  );
};

export default MainPage;
