import React, { useState } from 'react';
import Page1 from './Page1';
import Page2 from './Page2';
import Page3 from './Page3';

const App = () => {
  const [page, setPage] = useState(1);
  const [story, setStory] = useState('');
  const [quote, setQuote] = useState('');

  const handleNext = () => {
    setPage(2);
  };

  const handleSubmit = (story) => {
    setStory(story);
    // Replace this with your BERT model's emotion analysis
    const generatedQuote = "Be the change that you wish to see in the world.";
    setQuote(generatedQuote);
    setPage(3);
  };

  return (
    <div className="app">
      {page === 1 && <Page1 onNext={handleNext} />}
      {page === 2 && <Page2 onSubmit={handleSubmit} />}
      {page === 3 && <Page3 story={story} quote={quote} />}
    </div>
  );
};

export default App;
