import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [name, setName] = useState('');

  const handleLogin = () => {
    if (name.trim()) {
      onLogin(name);
      setName(''); // Clear the input field
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-green-900 text-white">
      <h1 className="text-4xl mb-4">Welcome to Quotify</h1>
      <input 
        type="text" 
        className="p-2 px-4 w-96 rounded-full bg-white text-black text-xl border-none outline-none mb-4"
        placeholder="Enter your name" 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
      />
      <button 
        className="p-2 px-4 rounded-full bg-white text-green-900 text-xl cursor-pointer transition duration-300 ease-in-out hover:bg-green-600 hover:text-white"
        onClick={handleLogin}
      >
        Login
      </button>
    </div>
  );
};

export default Login;
