import React, { useState } from 'react';

const Page2 = ({ onSubmit }) => {
  const [story, setStory] = useState('');

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-green-900 text-white">
      <h1 className="text-2xl">Quotify</h1>
      <p className="mt-2">Write down your story!</p>
      <input 
        type="text" 
        value={story} 
        onChange={(e) => setStory(e.target.value)} 
        className="mt-4 p-2 w-64 rounded bg-white text-black placeholder-gray-500" 
        placeholder="Type your story here..." 
      />
      <button 
        onClick={() => onSubmit(story)} 
        className="mt-4 p-2 bg-white text-black w-64 rounded"
      >
        &rarr;
      </button>
      <div className="absolute top-4 right-4">
        <button className="text-white text-2xl">&#8634;</button>
      </div>
    </div>
  );
};

export default Page2;
