import React, { useState } from 'react';
import Navbar from './Navbar';
import './Main.css'; // Import the CSS file

const Page1 = ({ userName, onPowerButtonClick }) => {
  const [inputText, setInputText] = useState('');
  const [quote, setQuote] = useState('');

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleSubmit = async () => {
    // Simulate sending the text to the model and receiving a quote
    const receivedQuote = await getQuoteFromModel(inputText);
    setQuote(receivedQuote);
  };

  // Mock function to simulate model response
  const getQuoteFromModel = async (text) => {
    // Replace this with actual model integration
    return "Be the change that you wish to see in the world. - Mahatma Gandhi";
  };

  return (
    <div className="page-container">
      <Navbar onPowerButtonClick={onPowerButtonClick} />
      <h1 className="header-text">Hello {userName},</h1>
      <p className="subheader-text">What do you feel today?</p>
      <div className="input-container">
        <input 
          type="text" 
          className="input-field" 
          value={inputText}
          onChange={handleInputChange}
          placeholder="T" 
        />
        <button className="submit-button" onClick={handleSubmit}>â†’</button>
      </div>
      {quote && <div className="quote-container">{quote}</div>}
    </div>
  );
};

export default Page1;
